mapper.insert:
INSERT INTO member (
	email, pwd, name, gender, 
	birthday, hp, zipcode, addr1, 
	addr2, regdate, state 
) VALUES (
	#{email}, MD5(#{pwd}), #{name}, #{gender}, 
	#{birthday}, #{hp}, #{zipcode}, #{addr1}, 
	#{addr2}, NOW(), #{state}
)

	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE member SET
pwd = MD5(#{pwd}),
name = #{name}, gender = #{gender}, birthday = #{birthday}, hp = #{hp}, zipcode = #{zipcode}, addr1 = #{addr1}, addr2 = #{addr2} WHERE no = #{no}
